mapper.select:
SELECT article.id,article.user_id,article.name,article.time,article.enabled,article.deleted, article.counts,author.nickname author_nickname, author.avatar_url author_avatar_url, group_concat(tags.id ORDER BY tags.id SEPARATOR ';') AS 'tags_id', group_concat(tags.content ORDER BY tags.id SEPARATOR ';') AS 'tags_content', group_concat(tags.counts ORDER BY tags.id SEPARATOR ';') AS 'tags_counts' FROM tb_article article, tb_user author, tb_tag_article tagArticle, tb_tags tags WHERE article.id IN (SELECT tb_tag_article.article_id FROM tb_tag_article WHERE tb_tag_article.tag_id = tb_tag_article.tag_id)
AND article.user_id = #{authorID}
AND article.user_id = author.id
AND article.enabled=#{enabled}
AND article.name Like concat('%',#{articleTitle},'%')
AND article.deleted=0 AND article.id = tagArticle.article_id AND tagArticle.tag_id = tags.id GROUP BY article.id

SELECT article.id,article.user_id,article.name,article.time,article.enabled,article.deleted, article.counts,author.nickname author_nickname, author.avatar_url author_avatar_url, null AS 'tags_id', null AS 'tags_content', null AS 'tags_counts' FROM tb_article article, tb_user author WHERE article.id NOT IN (SELECT tb_tag_article.article_id FROM tb_tag_article WHERE tb_tag_article.tag_id = tb_tag_article.tag_id) AND article.deleted=0
AND article.user_id = #{authorID}
AND article.user_id = author.id
AND article.enabled=#{enabled}
AND article.name Like concat('%',#{articleTitle},'%')
GROUP BY article.id

SELECT article.id,article.user_id,article.name,article.time,article.enabled,article.deleted, article.counts,author.nickname author_nickname, author.avatar_url author_avatar_url, author.avatar_url author_avatar_url, group_concat(tags.id ORDER BY tags.id SEPARATOR ';') AS 'tags_id', group_concat(tags.content ORDER BY tags.id SEPARATOR ';') AS 'tags_content', group_concat(tags.counts ORDER BY tags.id SEPARATOR ';') AS 'tags_counts' FROM tb_article article, tb_user author, tb_tag_article tagArticle, tb_tags tags WHERE article.id IN (SELECT tb_tag_article.article_id FROM tb_tag_article WHERE
tb_tag_article.tag_id = #{tagID}
tb_tag_article.tag_id = tb_tag_article.tag_id ) AND article.user_id = author.id AND tagArticle.article_id = article.id AND tagArticle.tag_id = tags.id AND article.deleted = 0 AND article.enabled = 1
AND article.name Like concat('%',#{articleTitle},'%')
GROUP BY article.id;

SELECT article.id,article.user_id,article.name,article.time,article.enabled,article.deleted, article.counts,author.nickname author_nickname, author.avatar_url author_avatar_url, null AS 'tags_id', null AS 'tags_content', null AS 'tags_counts' FROM tb_article article, tb_user author WHERE article.id NOT IN (SELECT tb_tag_article.article_id FROM tb_tag_article WHERE tb_tag_article.tag_id = tb_tag_article.tag_id) AND article.user_id = author.id AND article.deleted = 0 AND article.enabled = 1
AND article.name Like concat('%',#{articleTitle},'%')
GROUP BY article.id;
